CREATE OR REPLACE TRIGGER T_webjet_form_regular_exp BEFORE INSERT ON form_regular_exp
    FOR EACH ROW
        DECLARE
            val INTEGER|
        BEGIN
            IF :new.id IS NULL THEN
                SELECT S_webjet_form_regular_exp.nextval into val FROM dual|
                :new.id:= val|
            END IF|
        END|
;
ALTER TABLE form_regular_exp ADD id INT DEFAULT S_webjet_form_regular_exp.NEXTVAL NOT NULL;
ALTER TABLE form_regular_exp ADD PRIMARY KEY (ID);

