ALTER TABLE emails ADD recipient_user_id INTEGER;
     ALTER TABLE emails  MODIFY (recipient_user_id DEFAULT -1);
     CREATE TABLE emails_stat_click (click_id INTEGER NOT NULL, email_id INT NOT NULL, link NVARCHAR2(255) NOT NULL, click_date DATE NOT NULL, browser_id INTEGER, session_id INTEGER, PRIMARY KEY(click_id));
     CREATE SEQUENCE S_emails_stat_click START WITH 1;
     CREATE TRIGGER T_emails_stat_click BEFORE INSERT ON emails_stat_click
     FOR EACH ROW
     DECLARE
     val INTEGER|
     BEGIN
     IF :new.click_id IS NULL THEN
     SELECT S_emails_stat_click.nextval into val FROM dual|
     :new.click_id:= val|
     END IF|
     END|
     ;

