DROP TABLE IF EXISTS `quiz_answers`; DROP TABLE IF EXISTS `quiz_questions`; DROP TABLE IF EXISTS `quiz`;
CREATE TABLE `quiz` ( `id` INT(11) NOT NULL auto_increment, `name` VARCHAR(255) NOT NULL, PRIMARY KEY (`id`) );
CREATE TABLE `quiz_questions` ( `id` INT(11) NOT NULL auto_increment, `quiz_id` INT(11) NOT NULL, `sort_order` INT(11) NOT NULL, `question` VARCHAR(255) NOT NULL, `option1` VARCHAR(255) DEFAULT NULL, `option2` VARCHAR(255) DEFAULT NULL, `option3` VARCHAR(255) DEFAULT NULL, `option4` VARCHAR(255) DEFAULT NULL, `option5` VARCHAR(255) DEFAULT NULL, `option6` VARCHAR(255) DEFAULT NULL, `right_answer` INT(11) NOT NULL, PRIMARY KEY (`id`), KEY `quiz_id` (`quiz_id`) );
CREATE TABLE `quiz_answers` ( `id` INT(11) NOT NULL auto_increment, `form_id` VARCHAR(255) NOT NULL, `quiz_id` INT(11) NOT NULL, `quiz_question_id` INT(11) NOT NULL, `answer` INT(11) NOT NULL, `is_correct` INT(11) NOT NULL, `created` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP, PRIMARY KEY (`id`), KEY `quiz_id` (`quiz_id`), KEY `quiz_question_id` (`quiz_question_id`) );

IF OBJECT_ID('QUIZ_ANSWERS', 'U') IS NOT NULL DROP TABLE QUIZ_ANSWERS; IF OBJECT_ID('QUIZ_QUESTIONS', 'U') IS NOT NULL DROP TABLE QUIZ_QUESTIONS; IF OBJECT_ID('QUIZ', 'U') IS NOT NULL DROP TABLE QUIZ;
CREATE TABLE QUIZ ( ID DECIMAL(38, 0) NOT NULL identity(1,1), NAME NVARCHAR(255) NOT NULL, PRIMARY KEY(ID) );
CREATE TABLE QUIZ_ANSWERS ( ID DECIMAL(38, 0) NOT NULL identity(1,1), FORM_ID NVARCHAR(255) NOT NULL, QUIZ_ID DECIMAL(38, 0) NOT NULL, QUIZ_QUESTION_ID DECIMAL(38, 0) NOT NULL, ANSWER DECIMAL(38, 0) NOT NULL, IS_CORRECT DECIMAL(38, 0) NOT NULL, CREATED DATETIME DEFAULT getdate() NOT NULL, PRIMARY KEY(ID) );
CREATE TABLE QUIZ_QUESTIONS ( ID DECIMAL(38, 0) NOT NULL identity(1,1), QUIZ_ID DECIMAL(38, 0) NOT NULL, SORT_ORDER DECIMAL(38, 0) NOT NULL, QUESTION NVARCHAR(255) NOT NULL, OPTION1 NVARCHAR(255), OPTION2 NVARCHAR(255), OPTION3 NVARCHAR(255), OPTION4 NVARCHAR(255), OPTION5 NVARCHAR(255), OPTION6 NVARCHAR(255), RIGHT_ANSWER DECIMAL(38, 0) NOT NULL, PRIMARY KEY(ID) );

CREATE TABLE QUIZ ( ID NUMBER(*, 0) NOT NULL, NAME NVARCHAR2(255) NOT NULL, CONSTRAINT QUIZ_PK PRIMARY KEY ( ID ) USING INDEX ( CREATE UNIQUE INDEX QUIZ_PK ON QUIZ (ID ASC) ) ENABLE ); CREATE SEQUENCE S_QUIZ INCREMENT BY 1 MAXVALUE 99999999999999999999999999 MINVALUE 1 CACHE 20 ORDER;
CREATE TABLE QUIZ_ANSWERS ( ID NUMBER(*, 0) NOT NULL, FORM_ID NVARCHAR2(255) NOT NULL, QUIZ_ID NUMBER(*, 0) NOT NULL, QUIZ_QUESTION_ID NUMBER(*, 0) NOT NULL, ANSWER NUMBER(*, 0) NOT NULL, IS_CORRECT NUMBER(*, 0) NOT NULL, CREATED TIMESTAMP(6) DEFAULT sysdate NOT NULL, CONSTRAINT QUIZ_ANSWERS_PK PRIMARY KEY ( ID ) USING INDEX ( CREATE UNIQUE INDEX QUIZ_ANSWERS_PK ON QUIZ_ANSWERS (ID ASC) ) ENABLE ); CREATE SEQUENCE S_QUIZ_ANSWERS INCREMENT BY 1 MAXVALUE 99999999999999999999999999 MINVALUE 1 CACHE 20 ORDER;
CREATE TABLE QUIZ_QUESTIONS ( ID NUMBER(*, 0) NOT NULL, QUIZ_ID NUMBER(*, 0) NOT NULL, SORT_ORDER NUMBER(*, 0) NOT NULL, QUESTION NVARCHAR2(255) NOT NULL, OPTION1 NVARCHAR2(255), OPTION2 NVARCHAR2(255), OPTION3 NVARCHAR2(255), OPTION4 NVARCHAR2(255), OPTION5 NVARCHAR2(255), OPTION6 NVARCHAR2(255), RIGHT_ANSWER NUMBER(*, 0) NOT NULL, CONSTRAINT QUIZ_QUESTIONS_PK PRIMARY KEY ( ID ) USING INDEX ( CREATE UNIQUE INDEX QUIZ_QUESTIONS_PK ON QUIZ_QUESTIONS (ID ASC) ) ENABLE ); CREATE SEQUENCE S_QUIZ_QUESTIONS INCREMENT BY 1 MAXVALUE 99999999999999999999999999 MINVALUE 1 CACHE 20 ORDER;
CREATE TRIGGER T_quiz BEFORE INSERT ON quiz FOR EACH ROW DECLARE val INTEGER| BEGIN IF :new.id IS NULL THEN SELECT s_quiz.nextval into val FROM dual| :new.id:= val| END IF| END|;
CREATE TRIGGER T_quiz_answers BEFORE INSERT ON quiz_answers FOR EACH ROW DECLARE val INTEGER| BEGIN IF :new.id IS NULL THEN SELECT s_quiz_answers.nextval into val FROM dual| :new.id:= val| END IF| END|;
CREATE TRIGGER t_quiz_questions BEFORE INSERT ON quiz_questions FOR EACH ROW DECLARE val INTEGER| BEGIN IF :new.id IS NULL THEN SELECT s_quiz_questions.NEXTVAL INTO val FROM dual| :new.id := val| END IF| END|;

