ALTER TABLE webjet_conf_prepared ADD user_id INT null;
CREATE SEQUENCE S_webjet_conf_prepared START WITH 1;
CREATE TRIGGER T_webjet_conf_prepared BEFORE INSERT ON webjet_conf_prepared
    FOR EACH ROW
        DECLARE
            val INTEGER|
        BEGIN
            IF :new.id IS NULL THEN
                SELECT S_webjet_conf_prepared.nextval into val FROM dual|
                :new.id:= val|
            END IF|
        END|
;
ALTER TABLE webjet_conf_prepared ADD id INT DEFAULT S_webjet_conf_prepared.NEXTVAL NOT NULL;
ALTER TABLE webjet_conf_prepared ADD (CONSTRAINT id PRIMARY KEY (ID));
